mapper.delete:
delete from exam_publish_record

    id=#{id}
    and version=#{version}

mapper.select:
SELECT r.id, p.paper_id, p.exam_session, p.created_time, r.tel, r.examiner, r.actual_end_time, p.marking_stop_time, r.objective_subject_score, r.subjectvie_subject_score, r.system_evaluate, r.status FROM exam_record AS r, exam_publish_record AS p
r.exam_publish_record_id=p.id and r.user_id=#{userId} and r.actual_end_time > "${endTimeRange.get(0)}" and r.actual_end_time < "${endTimeRange.get(1)}"
and p.exam_session like "${examSession}%" and r.status=#{status}
